import {createSlice} from '@reduxjs/toolkit';

const initialState = {
    clientName: '',
    clientSurname: '',
    clientEmail: '',
};

const clientSlice = createSlice({
    name: 'clientSlice',
    initialState,
    reducers: {
        changeClientInfo: (state, action) => {
            state[action.payload.key] = action.payload.value;
        },
    },
})

export const {changeClientInfo} = clientSlice.actions;
export default clientSlice.reducer;
