// reducers.js

import { SET_LANGUAGE, TOGGLE_AUTH,  SET_CLIENT, SET_NAME, SET_SURNAME } from './actions';

const Language = {
    English: 'English',
    French: 'French',
    Spanish: 'Spanish',
};

const initialState = {
    language: Language.English,
    isLogged: true,
    client: {},
    name: '',
    surname: '',
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_CLIENT:
            return {
                ...state,
                client: action.payload,
            };

        // The rest of your code...

        case SET_LANGUAGE: {
            let newLanguage;
            if (state.language === Language.English) {
                newLanguage = Language.French;
            } else if (state.language === Language.French) {
                newLanguage = Language.Spanish;
            } else {
                newLanguage = Language.English;
            }
            return {
                ...state,
                language: newLanguage,
            };
        }

        case TOGGLE_AUTH:
            return {
                ...state,
                isLogged: !state.isLogged,
            };

        default:
            return state;
    }
};

export default rootReducer;
