// AuthButton.js
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { login, logout} from '../redux/loginSlice';

function AuthButton() {
    const dispatch = useDispatch();
    let isLogged = useSelector(state => state.userStatus.isLogged);
    let buttonStyle =   {
        backgroundColor: isLogged ? 'green' : 'red',
        color: 'white',
        border: 'none',
        padding: '10px 20px',
        cursor: 'pointer',
    };

    function changeLogged() {
        if (isLogged === true) {
            dispatch(logout())
        } else {
            dispatch(login())
        }
        console.log(isLogged); // This will always log the previous value of isLogged
    }

    return (
        <button style={buttonStyle} onClick={changeLogged}>
            {isLogged ? 'Logout' : 'Login'}
        </button>
    );
}

export default AuthButton;
