// reducers.js
import { SET_LANGUAGE, TOGGLE_AUTH } from './actions';

const initialState = {
    language: 'English',
    isLogged: false,
};

const languageCycle = ['English', 'French', 'Spanish'];

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_LANGUAGE:
            return {
                ...state,
                language: languageCycle[(languageCycle.indexOf(state.language) + 1) % languageCycle.length],
            };
        case TOGGLE_AUTH:
            return {
                ...state,
                isLogged: !state.isLogged,
            };
        default:
            return state;
    }
};

export default rootReducer;
