import {createSlice} from '@reduxjs/toolkit';

// Initial state for client info
const initialState = {
    clientName: '',
    clientSurname: '',
    clientEmail: '',
};

// Slice for handling client info
const clientSlice = createSlice({
    name: 'clientSlice',
    initialState,
    reducers: {
        // Action for changing client info
        changeClientInfo: (state, action) => {
            // Destructure payload for better readability
            const {field, value} = action.payload;
            state[field] = value;
        },
    },
});

// Exporting the action and the reducer
export const {changeClientInfo} = clientSlice.actions;
export default clientSlice.reducer;
