import {useSelector} from 'react-redux';

import {informationTextFr, informationTextEn, informationTextSp} from "./informationsText";
import {discoveryTextFr, discoveryTextEn, discoveryTextSp} from "./discoveryText";


function dictTemplate() {

    // Constants
    const FLAGS_URLS = [
        'https://www.svgrepo.com/show/365915/france.svg',
        'https://www.svgrepo.com/show/365950/usa.svg',
        'https://www.svgrepo.com/show/365930/spain.svg'
    ];
    const LANGUAGE_TAGS = [
        'fr',
        'en',
        'sp'
    ];
    const discovery = [
        discoveryTextFr,
        discoveryTextEn,
        discoveryTextSp
    ];
    const informationText = [
        informationTextFr,
        informationTextEn,
        informationTextSp
    ];
    const ticketText = [
        'Billets',
        'Tickets',
        'Boletos'
    ];
    const homeText = [
        'Accueil',
        'Home',
        'Casa'
    ];
    const qrCodeText = [
        'QR Code',
        'QrCode',
        'Código QR'
    ];
    const loginText = [
        'Connexion',
        'Login',
        'Inicio de sesión'
    ];
    const languageText = [
        'Fr',
        'En',
        'Es'
    ];
    const logoutText = [
        'Déconnexion',
        'Logout',
        'Cerrar sesión'
    ];

    // Number and language

    return {
        flagD: FLAGS_URLS,
        buttonLanguageTextD: LANGUAGE_TAGS,
        mainTitleD: 'Paris 2024',
        componentsD: [discovery, ticketText, homeText, qrCodeText, informationText],
        componentsNameD: ['discovery', 'tickets', 'home', 'qrcode', 'informations'],
        discoveryD: discovery,
        ticketsD: ticketText,
        homeD: homeText,
        qrCodeD: qrCodeText,
        informationsD: informationText,
        loginD: loginText,
        languageD: languageText,
        logoutD: logoutText,
    };
}

export function Translations() {
    const language = useSelector(state => state.language);
    return dictTemplate(language);
};

export default Translations;
