import React, {useState, useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {languageReducer, getNextLanguage} from '../redux/languageSlice';
import {ENGLISH, FRENCH, SPANISH} from '../constants/constants';

function LanguageButton() {
    const dispatch = useDispatch();
    const language = useSelector(state => state.language);
console.log('LanguageButton')
    const [languageText, setLanguageText] = useState('English');

    const switchLanguage = () => {
        const nextLanguage = getNextLanguage(language);
        dispatch({type: 'language/change', payload:nextLanguage});
    }


    useEffect(() => {
        if (language === ENGLISH) {
            setLanguageText('English');
        } else if (language === FRENCH) {
            setLanguageText('Fançais');
        } else {
            setLanguageText('Español');
        }
    }, [language]);

    return (
        <button onClick={switchLanguage}>
            {languageText}
        </button>
    );
}

export default LanguageButton;
