// LanguageButton.js
import React from 'react';
import {useDispatch, useSelector} from 'react-redux';
import {setLanguage} from '../redux/languageSlice';
import {getNextLanguage} from '../functions/nextLanguage';
import {ENGLISH, FRENCH, SPANISH} from "../constants/constants";

function LanguageButton() {
    const dispatch = useDispatch();
    const language = useSelector(state => state.language);

    const changeLanguage = () => {
        let theNextLanguage = language;
        switch(theNextLanguage) {
            case FRENCH:
                theNextLanguage = ENGLISH;
                break;
            case ENGLISH:
                theNextLanguage = SPANISH;
                break;
            case SPANISH:
                theNextLanguage = FRENCH;
                break;
            default:
                theNextLanguage = ENGLISH;
        }
    };

    return (
        <button onClick={changeLanguage}>
            Change Language (Current: {language.type})
        </button>
    );
};
export default LanguageButton;
