import {useActualDict} from '../constants/Translations';

import axios from 'axios';
import apiService from '../Controller/apiService.js';

import { useSelector, useDispatch } from 'react-redux';
import { SET_CLIENT, SET_NAME, SET_SURNAME } from '../redux/actions';
import '../redux/reducers.js';

function QrCode() {

    const [qrCode, setQrCode] = useState('');

    const ClientComponent = () => {

        const handleGetQrCode = async () => {
            try {
                if ((!qrCode) || (client.isLogged === false)) {
                    alert('You must be logged in to get a QR Code.');
                    return;
                }
                const response = await apiService.getQRCode(qrCode);
                setQrCode(response.data);
                const qrCode = {name, surname, email, password};
                console.log('Client created:', response.data);
            } catch (error) {
                console.error('Error creating client:', error);
            }
        }
    }

        return (

        <div className='form-container'>
            <img src={name.qrCode} alt="QR Code" className="qrcode-container"/>
            <div className='button-container'>
                <button type='submit' className='button-itself'>Display QR Code</button>
                <button type='submit' className='button-itself'>Print Ticket</button>
            </div>
        </div>
    );
}

export default QrCode;
