import React, { useContext } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import UpperBar from './components/UpperBar';
import LoginAndLanguageBar from "./components/LoginAndLanguageBar";
import NavigationBar from "./components/NavigationBar";
import Zone from "./components/ZoneBar";

import home from './routes/home';
import discovery from './routes/discovery';
import tickets from './routes/tickets';
import informations from './routes/informations';
import qrcode from './routes/qrcode';

import Discovery from "./LowerZoneComponents/Discovery";
import Tickets from "./LowerZoneComponents/Tickets";
import Informations from "./LowerZoneComponents/Informations";
import QrCode from "./LowerZoneComponents/QrCode";
import Home from "./LowerZoneComponents/Home";


import { Provider } from 'react-redux';
import store from './redux/store';

import { LanguageContext } from './contexts/LanguageContext';


import { useSelector}  from "react-redux";

import Translations from './constants/Translations';


function Content() {
    let actualLanguage = useSelector(state => state.language);
    console.log('dans App.js, Language actuelle :', actualLanguage);
    let dict = Translations(actualLanguage);
    const location = useLocation();
    const type = location.pathname.slice(1); // remove the leading slash

    return (
        <div>
            <UpperBar />
            <LoginAndLanguageBar />
            <NavigationBar />
            <Zone type={type} />
            <Routes>
                <Route path="/home" element={<Home />} />
                <Route path="/discovery" element={<Discovery />} />
                <Route path="/tickets" element={<Tickets />} />
                <Route path="/informations" element={<Informations />} />
                <Route path="/qrcode" element={<QrCode />} />

                <Route path="/" element={<Navigate to="/home" replace />} />
            </Routes>
        </div>
    );
}

function App() {

    const [language, setLanguage] = React.useState(state => state.language);

    return (
        <Provider store={store}>
            <LanguageContext.Provider value={{language, setLanguage }}>
                <Router>
                    <Content />
                </Router>
            </LanguageContext.Provider>
        </Provider>
    );
}

export default App;
