// AuthButton.js
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import'../redux/loginSlice';

function AuthButton() {
    const dispatch = useDispatch();
    let isLogged = useSelector(state => state.isLogged);

    const buttonStyle =   {
        backgroundColor: isLogged ? 'green' : 'red',
        color: 'white',
        border: 'none',
        padding: '10px 20px',
        cursor: 'pointer',
    };

    function changeLogged() {
        if (isLogged === true) {
            isLogged = false;
        } else {
            isLogged = true;
        }
        dispatch(setLogged(isLogged));
    }

    return (
        <button style={buttonStyle} onClick={changeLogged}>
            {isLogged}
        </button>
    );
}

export default AuthButton;
