        } catch(err) {
            throw new Error('Error submitting tickets:', err);
        }
    }



    // Change the ticket number

    const handleTicketChange = (event) => {
        setTicketNumber(parseInt(event.target.value));
    };


    return (

        <div>
            <p> Choix du billet </p>
            <div>
                <h2>Choose Tickets</h2>
                <div>
                    <input
