import "../componentsStyles/LowerZone.css";
import '../componentsStyles/TicketsStyle.css';
import axios from 'axios';


import {useActualDict} from '../constants/Translations';

import { useSelector } from 'react-redux';
import {useState} from "react";
import apiService from '../Controller/apiService.js';


function Tickets() {

    let [ticketNumber, setTicketNumber] = useState(0);
    const [tickets, setTickets] = useState([]);


    const handleTicketSubmit = async () => {
        try {

            const response = await apiService.postTicket({ tickets });
            console.log('Tickets submitted:', response.data);
        } catch (error) {
            console.error('Error submitting tickets:', error);
        }
    };

    const handleTicketChange = (event) => {
        setTicketNumber(parseInt(event.target.value));
    };


    return (

        <div>
            <p> Choix du billet </p>
            <div>
                <h2>Choose Tickets</h2>
                <div>
                    <input
                        type="radio"
                        value="1"
                        checked={ticketNumber === 1 ||  ticketNumber === 0}
                        onChange={handleTicketChange}
                    /> 1 Ticket (20 euros)
                </div>
                <div>
                    <input
                        type="radio"
                        value="2"
                        checked={ticketNumber === 2}
                        onChange={handleTicketChange}
                    /> 2 Tickets (30 euros)
                </div>
                <div>
                    <input
                        type="radio"
                        value="4"
                        checked={ticketNumber === 4}
                        onChange={handleTicketChange}
                    /> 4 Tickets (40 euros)
                </div>
                <button onClick={handleTicketSubmit}>Submit Tickets</button>
            </div>
        </div>
    );
}

export default Tickets;
