// reducers.js
import { SET_LANGUAGE, TOGGLE_AUTH,  SET_CLIENT, SET_NAME, SET_SURNAME } from './actions';

const initialState = {
    language: 'English',
    isLogged: true,
    client: null,
    name: '',
    surname: '',
};

const languageCycle = ['English', 'French', 'Spanish'];

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_CLIENT:
            return {
                ...state,
                client: action.payload,
            };
        case SET_NAME:
            return {
                ...state,
                name: action.payload,
            };
        case SET_SURNAME:
            return {
                ...state,
                surname: action.payload,
            };
        case SET_LANGUAGE:
            {
                let newLanguage;
                if (state.language === 'English') {
                    newLanguage = 'French';
                } else if (state.language === 'French') {
                    newLanguage = 'Spanish';
                } else {
                    newLanguage = 'English';
                }
                return {
                    ...state,
                    language: newLanguage,
                };
            }
            case TOGGLE_AUTH:
                {
                    let isLogged = state.isLogged;
                    if (isLogged == true) {
                        isLogged = false;
                    }

                    return {
                        ...state,
                        isLogged: state.isLogged
                    };
                }

        default:
            return state;
    }
};

export default rootReducer;
