// src/redux/languageSlice.js
import { createSlice } from '@reduxjs/toolkit';
import { ENGLISH, FRENCH, SPANISH } from '../constants/constants';

// Extracted function
function getNextLanguage(currentLanguage) {
    if (currentLanguage === ENGLISH) {
        return FRENCH;
    } else if (currentLanguage === FRENCH) {
        return SPANISH;
    } else {
        return ENGLISH;
    }
}

// Initial state
const initialState = {
    language: ENGLISH,
};

// Slice
const languageSlice = createSlice({
    name: 'languageSlice', // Set initial state
    initialState,
    reducers: {
        name:'languageSlice',
        initialState,
        languageReducer(state) {
            state.language = getNextLanguage(state.language);
        }
    },
});

export const { languageReducer} = languageSlice.actions;
export {getNextLanguage};
export default languageSlice.reducer;
