// AuthButton.js
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {userReducer} from '../redux/loginSlice';

function AuthButton() {
    const dispatch = useDispatch();
    let isLogged = useSelector(state => state.isLogged);
    let buttonColor;
    let buttonText;
    console.log('isLogged after useSelector:', isLogged);

    function changeLogged() {
        isLogged = !isLogged;
        switch(isLogged) {
            case true:
                buttonColor = 'green';
                buttonText = 'Connected';
                break;
            case false:
                buttonColor = 'red';
                buttonText = 'Disconnected';
                break;
        }
        console.log('isLogged after x = !x; :', isLogged);
        dispatch(userReducer());
        console.log('isLogged after dispatch',isLogged);
    }

    return(
        <button
            style={{
                color: 'white',
                border: 'none',
                padding: '10px 20px',
                cursor: 'pointer'
            }}
            backgroundColor={buttonColor}
            onClick={buttonText}
        >
            {isLogged}
        </button>
    );
}

export default AuthButton;
