import {createSlice} from '@reduxjs/toolkit';

    const initialState = {
        clientName : '',
        clientSurname: '',
        clientEmail: '',
    };

    const clientSlice = createSlice({
        name: 'clientSlice',
        initialState,
        reducers: {
            changeName(state, action) {
                state.clientName = action.payload;
            },
            changeSurname(state, action) {
                state.clientSurname = action.payload;
            },
            changeEmail(state, action) {
                state.clientEmail = action.payload;
            },
        }
    })


export const { changeName, changeSurname, changeEmail } = clientSlice.actions;

export default clientSlice.reducer;
;
