// src/redux/languageSlice.js
import { createSlice } from '@reduxjs/toolkit';
import { ENGLISH, FRENCH, SPANISH } from '../constants/constants';

// Extracted function
export function getNextLanguage(currentLanguage) {
    switch (currentLanguage) {
        case ENGLISH:
            return FRENCH;
        case FRENCH:
            return SPANISH;
        case SPANISH:
            return ENGLISH;
        default:
            return ENGLISH;
    }
}

// Initial state
const initialState = {
    language: ENGLISH,
};

// Slice
const languageSlice = createSlice({
    name: 'language', // Set initial state
    initialState,
    reducers: {
        languageReducer(state) {
            return { ...state, language: getNextLanguage(state.language) };
        }
    },
});

export const { languageReducer} = languageSlice.actions;
export default languageSlice.reducer;
