// LanguageButton.js
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setLanguage } from '../redux/actions';

function LanguageButton() {
    const dispatch = useDispatch();
    const language = useSelector(state => state.language);

    const changeLanguage = () => {
        // Here you should define the logic to change the language
        // For example, if you have only two languages (English and French), you can do:
        const newLanguage = language === 'English' ? 'French' : 'English';
        dispatch(setLanguage(newLanguage));
    };

    return (
        <button onClick={changeLanguage}>
            Change Language (Current: {language})
        </button>
    );
};

export default LanguageButton;
