// AuthButton.js
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {userReducer} from '../redux/loginSlice';

function AuthButton() {
    const dispatch = useDispatch();
    let isLogged = useSelector(state => state.isLogged);
    var buttonColor;
    var  buttonText;
    console.log('isLogged after useSelector:', isLogged);

    function changeLogged() {
        isLogged = !isLogged;
        if (isLogged === true)
        {
            buttonColor = 'green';
            buttonText = 'Connected';
        }
        else if (isLogged === false)
        {
            buttonColor = 'red';
            buttonText = 'Disconnected';
        }
        else
        {
            buttonColor = 'green';
            buttonText = 'Connected';
        }
        console.log('isLogged after x = !x; :', isLogged);
        dispatch(userReducer());
        console.log('isLogged after dispatch',isLogged);
    }

    return(
        <button
            style={{
                color: 'white',
                border: 'none',
                padding: '10px 20px',
                cursor: 'pointer'
            }}
            backgroundColor={buttonColor}
            onClick={changeLogged}
        >
            {buttonText}
        </button>
    );
}

export default AuthButton;
