import {useSelector} from 'react-redux';

import {informationTextFr, informationTextEn, informationTextSp} from "./informationsText";
import {discoveryTextFr, discoveryTextEn, discoveryTextSp} from "./discoveryText";

function dictTemplate(flagD, buttonLanguageTextD, mainTitleD, discoveryD, ticketsD, homeD, qrCodeD, informationsD, loginD, languageD, logoutD) {
    return {
        flagD,
        buttonLanguageTextD,
        mainTitleD: 'Paris 2024',
        componentsD: [discoveryD, ticketsD, homeD, qrCodeD, informationsD],
        componentsNameD: ['discovery', 'tickets', 'home', 'qrcode', 'informations'],
        discoveryD,
        ticketsD,
        homeD,
        qrCodeD,
        informationsD,
        loginD,
        languageD,
        logoutD,
        // (Text data is omitted for brevity ...)
    };
}

export function Translations() {
    const language = useSelector(state => state.language);
    const dictFrench = dictTemplate(
        'https://www.svgrepo.com/show/365915/france.svg',
        'Anglais',
        discoveryTextFr,
        'Billets',
        'Accueil',
        'QR Code',
        informationTextFr,
        'Connexion',
        'Fr',
        'Déconnexion'
    );
    const dictEnglish = dictTemplate(
        'https://www.svgrepo.com/show/365950/usa.svg',
        'Español',
        discoveryTextEn,
        'Tickets',
        'Home',
        'QrCode',
        informationTextEn,
        'Login',
        'En',
        'Logout'
    );
    const dictSpanish = dictTemplate(
        'https://www.svgrepo.com/show/365930/spain.svg',
        'Français',
        discoveryTextSp,
        'Boletos',
        'Casa',
        'Código QR',
         informationTextSp,
        'Inicio de sesión',
        'Es',
        'Cerrar sesión',
    );
    switch (language) {
        case 'fr': return dictFrench;
        case 'es': return dictSpanish;
        default: return dictEnglish;
    }
}

export default Translations;
