import "../componentsStyles/LowerZone.css";
import "../componentsStyles/QrCodeStyle.css";
import imageQR from '../images/imageQrCodeExample.png';

import {useActualDict} from '../constants/Translations';

import axios from 'axios';
import apiService from '../Controller/apiService.js';

import { useSelector, useDispatch } from 'react-redux';
import {useState} from "react";
import {setName} from '../redux/nameSlice';
import {setSurname} from '../redux/surnameSlice';
import {setEmail} from '../redux/emailSlice';
import {login} from '../redux/loginSlice';


function QrCode() {

    const name = useSelector(state => state.name);
    const surname = useSelector(state => state.surname);
    const email = useSelector(state => state.email);
    const login = useSelector(state => state.login);

    let qrCode = null;        // this is a mockup, the real qrCode will be fetched from the backend

    // imageQR is a mockup, the real qrCode will be fetched from the backend

    const handleGetQrCode = async () => {
        try {
            if ((qrCode === null) || (login === false)) {
                alert('You must be logged in to get a QR Code. You have to bought a ticket, too.');
            } else {
                console.log('Here is your qrCode');
            }
        } catch (error) {
            console.error('Error creating client:', error);
        }
    }

// Function to display the ticket, and to print it
// They are mockups, the real ones will be implemented in the future

        const displayTicket = () => {
            alert('Displaying ticket');
        }

        const printTicket = () => {
            alert('Printing ticket');
        }

        return (

        <div className='form-container'>
            <img src={imageQR} alt="QR Code" className="qrcode-container"/>
            <div className='button-container'>
                <button type='submit'
                            className='button-itself'
                            onClick={displayTicket}>Display QR Code</button>

                <button type='submit'
                            className='button-itself'
                            onClick={printTicket}>Print Ticket</button>
            </div>
        </div>
    );
}

export default QrCode;
