import React, {useState, useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {languageReducer, getNextLanguage} from '../redux/languageSlice';
import {ENGLISH, FRENCH, SPANISH} from '../constants/constants';

function LanguageButton() {
    const dispatch = useDispatch();

console.log('LanguageButton language 1) : ', language);
