import {createSlice} from '@reduxjs/toolkit';

const initialState = {
    isLoggedIn: false,
};

export const userSlice = createSlice({
    name: 'user',
    initialState,
    reducers: {
        login(userState) {
            userState.isLoggedIn = true;
        },
        logout(userState) {
            userState.isLoggedIn = false;
        },
    },
});

export const {login, logout} = userSlice.actions;

export default userSlice.reducer;
