// AuthButton.js
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {userReducer} from '../redux/loginSlice';

function AuthButton() {
    const dispatch = useDispatch();
    let isLogged = useSelector(state => state.isLogged);
    console.log('isLogged after useSelector:', isLogged);
    let buttonStyle =   {
        backgroundColor: isLogged === true ? 'green' : 'red',
        color: 'white',
        border: 'none',
        padding: '10px 20px',
        cursor: 'pointer',
    };

    function changeLogged() {
        isLogged = !isLogged;
        console.log('isLogged after x = !x; :', isLogged);
        dispatch(userReducer());
        console.log('isLogged after dispatch',isLogged);
    }

    return (
        <button style={buttonStyle} onClick={changeLogged}>
            {isLogged ? 'Log out' : 'Log in'}
        </button>
    );
}

export default AuthButton;
