// src/redux/languageSlice.js
import { createSlice } from '@reduxjs/toolkit';

// Introduced constants
const ENGLISH = 0;
const SPANISH = 1;
const FRENCH = 2;

// Extracted function
const getNextLanguage = (currentLanguage) => {
    if (currentLanguage === ENGLISH) {
        return FRENCH;
    } else if (currentLanguage === FRENCH) {
        return SPANISH;
    } else {
        return ENGLISH;
    }
}

// Initial state
const initialState = {
    language: ENGLISH,
};

// Slice
const languageSlice = createSlice({
    name: 'languageSlice', // Set initial state
    initialState,
    reducers: {
        setLanguage(state) {
            state.language = getNextLanguage(state.language);
        }
    },
});

export const { setLanguage } = languageSlice.actions;

export default languageSlice.reducer;
