// AuthButton.js
import React, {useState, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import {userReducer} from '../redux/loginSlice';

function updateButtonStatus(isLogged, setButtonColor, setButtonText) {
    if (!isLogged) {
        setButtonColor('green');
        setButtonText('Connected');
    } else {
        setButtonColor('red');
        setButtonText('Disconnected');
    }
}

function AuthButton() {
    const dispatch = useDispatch();
    let isLogged = useSelector(state => state.isLogged);
    const [buttonColor, setButtonColor] = useState('red');
    const [buttonText, setButtonText] = useState('Disconnected');

    useEffect(() => {
        updateButtonStatus(isLogged, setButtonColor, setButtonText);
    }, [isLogged, setButtonColor, setButtonText]); // added setButtonColor and setButtonText as dependencies

    function changeLogged() {
        dispatch(userReducer());
    }

    return (
        <button
            style={{
                backgroundColor: buttonColor,
                color: 'white',
                border: 'none',
                padding: '10px 20px',
                cursor: 'pointer'
            }}
            onClick={changeLogged}
        >
            {buttonText}
        </button>
    );
}

export default AuthButton;
