import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    isLogged: false,
};

export const loginSlice = createSlice({
    name: 'loginSlice',
    initialState,
    reducers: {
        login(state) {
            state.isLogged = true;
        },
        logout(state){
            state.isLogged = false;
        },
    },
});

export const { login, logout} = loginSlice.actions
export default loginSlice.reducer
